INSERT INTO `about_belarus` (`about_id`, `about_name`, `about_img`, `about_link`, `about_text`, `keywords`, `description`, `title`, `position`, `visible`) VALUES
(1, 'Краткая информация', 'belarus_34.jpg', 'about_belarus.php?id=1', '		<p><span class="bold">Официальное название государства:</span> Республика Беларусь</p>\n				<p><span class="bold">Столица:</span> Минск – 1 млн 974,8 тыс. человек</p>\n				<p><span class="bold">Государственные языки:</span> белорусский, русский</p>\n				<p><span class="bold">Код страны:</span> BLR</p>\n				<p><span class="bold">Часовой пояс:</span> UTC+03:00</p>\n				<p><span class="bold">Валюта:</span> белорусский рубль (BYN), состоит из 100 копеек</p>\n				<p><span class="bold">Политический строй:</span> президентская республика</p>\n				\n				<p><span class="bold">Президент:</span> Александр Лукашенко (1994)</p>\n				<p><span class="bold">Премьер-министр:</span> Андрей Кобяков (2014)</p>\n				<p><span class="bold">Территория:</span> 207,6 тыс. кв. км</p>\n				<p><span class="bold">Население:</span> численность – 9 млн 495,5 тыс. человек (на 1 июля 2017 г.)</p>\n					<p>доля городского населения – 77,9%</p>\n				<p><span class="bold">Территориальное деление</span> - 6 областей: Брестская, Витебская, Гомельская, Гродненская, Минская, Могилевская</p>\n				\n				<p><span class="bold">Национальности:</span> белорусы – 83,7%, русские – 8,3%, поляки – 3,1%, украинцы – 1,7%, евреи – 0,1%, другие национальности – 3,1% (перепись, 2009 год)</p>		\n			\n				<!--<p><span class="bold">Главные отрасли промышленности: </span> металлургия, металлообработка, машиностроение, химическая и нефтехимическая, легкая, пищевая</p>\n				<p><span class="bold">Сельское хозяйство </span> специализируется на выращивании зерновых, картофеля, овощей, сахарной свеклы, льна, мясо-молочном животноводстве</p>\n				<p><span class="bold">Природные ресурсы:</span> древесина, торф, калийная и каменная соли, небольшие месторождения нефти и природного газа, гранит, доломит, известняк, глина, песок</p>\n					\n				<p>Беларусь расположена на западе Восточно-Европейской равнины, граничит с пятью государствами: Россия, Украина, Польша, Литва, Латвия. Протяженность территории  с севера на юг – 560 км, с запада на восток – 650 км. Территория страны в основном равнинная. Большинство возвышенностей (не превышающих 300 м) размещается в центре и на Северо-Западе, юг занимает большая заболоченная Полесская низменность</p>	-->\n				\n', 'Беларусь, Белоруссия, краткая информация о Беларуси, президент Беларуси, население Беларуси, деньги Беларуси, погода в Беларуси', 'Краткая информация о Беларуси, президент, деньги, погода, население', 'Краткие факты о Беларуси', 1, 1),
(2, 'История', 'belarus_30.jpg', '', '', '', '', 'История Беларуси', 2, 1),
(3, 'Культура', 'culture.jpg', '', '', 'культура Беларуси, белорусская культура, самобытность белорусов, национальный белорусский колорит, Беларусь', 'Культура Беларуси - история, изобразительное искусство, музыка, театр, кино, литература', 'Культура Беларуси', 3, 1),
(4, 'Религия', 'bel07-1024x522.jpg', '', '<p>Жители Беларуси свободно исповедуют множество религий</p>', 'Религия в Беларуси, православие в Беларуси, католицизм в Беларуси, протестантизм в Беларуси, баптизм в Беларуси, история белорусской религии', 'Религия в Беларуси - конфессии, история', 'Религия Беларуси', 4, 1);

