--
-- Структура таблицы `article`
--

CREATE TABLE IF NOT EXISTS `article` (
  `article_id` int(11) NOT NULL AUTO_INCREMENT,
  `article_date` char(10) NOT NULL,
  `article_name` char(255) NOT NULL,
  `article_img` char(255) NOT NULL,
  `article_link` char(255) NOT NULL,
  `article_text` text NOT NULL,
  `article_rating` int(11) NOT NULL,
  `article_description` char(255) NOT NULL,
  `article_cat_id` int(11) NOT NULL,
  `panorama_link` varchar(800) NOT NULL,
  `date` datetime NOT NULL,
  `keywords` text NOT NULL,
  `description` text NOT NULL,
  `title` char(255) NOT NULL,
  `position` int(11) NOT NULL,
  `visible` int(11) NOT NULL,
  PRIMARY KEY (`article_id`),
  KEY `article_id` (`article_id`),
  KEY `article_cat_id` (`article_cat_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=7 ;

--
-- Дамп данных таблицы `article`
--

INSERT INTO `article` (`article_id`, `article_date`, `article_name`, `article_img`, `article_link`, `article_text`, `article_rating`, `article_description`, `article_cat_id`, `panorama_link`, `date`, `keywords`, `description`, `title`, `position`, `visible`) VALUES
(1, '20.08.2017', 'Фестиваль "Путь викингов"', 'vikingi4-1.jpg', '', '	<p>19-20 августа 2017 в живописном месте – Парке истории «Сула» прошел Второй международный фестиваль раннего средневековья «Путь Викингов». Фестиваль был посвящен эпохе легендарного торгового пути «из варяг в греки», который оказал огромное влияние на историю наших земель.</p>\r\n\r\n			<p>Фестиваль длился два насыщенных дня. Гостей ждала интересная программа, включающая воинские состязания: одиночные и групповые поединки, битва на мосту, захват щита.</p>\r\n\r\n			<p>Кульминацией стала театрализованная реконструкция штурма укрепленного поселения с захватом Драккара — деревянного корабля викингов. 3 марта 1067 года произошло самое кровавое сражение белорусского раннего Средневековья – битва на Немиге. Сражение между войсками полоцкого князя Всеслава Чародея из династии Рогволодовичей с войсками Ярославовичей (киевким, черниговским и переяславским князьями). Ярославичи вторглись в Полоцкое княжество и вышли на реку Немигу, где их встретил Всеслав Чародей…</p>\r\n\r\n			<p>В фестивале приняли участие более двадцати реконструкторских клубов из разных городов. Поистине, увлекающее зрелище! Это нужно было видеть своими глазами!</p>\r\n\r\n			<p>Окружающий пейзаж деревни Сула — впечатляет до глубины души и погружает в атмосферу происходящих событий.</p>\r\n			\r\n			<p>Лагерь викингов с огромными палатками и шатрами, где готовили еду прямо на костре. Город мастеров с большим выбором оригинальных сувениров, а также мастер-классы по гончарству, оружейному делу, обработке камней и т.д. — не оставили никого равнодушными!</p>\r\n\r\n			<p>В августовскую жару — окунуться в воду на пляже было особым удовольствием. Также, можно было посмотреть на парк истории Сула с воздуха — для посетителей фестиваля был вертолет. Также все желающие могли прокатиться на корабле викингов — Драккаре! «Корабль-дракон» — длинный и узкий, с высоко поднятыми носом и кормой.</p>\r\n\r\n			<p>Первый день закончился концертом групп: Неврида, Спайр, Сакрамант, Вандрарен, Фолькор, Племя. Между выступлениями музыкальных групп был небольшой перерыв на фаершоу.</p>', 2, '19-20 августа 2017 в живописном месте – Парке истории «Сула» прошел Второй международный фестиваль раннего средневековья «Путь Викингов». Фестиваль был посвящен эпохе легендарного торгового пути «из варяг в греки», который оказал огромное влияние на ис...', 1, '<style type="text/css">.kuula7lyyy { width: 100%; height: 500px; max-width: 100%; max-height: 100vw; }</style><iframe class="kuula7lyyy" frameborder="0" scrolling="no" allowfullscreen="true" src="https://kuula.co/share/7lYyY?thumbs=0&logo=0"></iframe>', '2017-08-20 10:00:00', 'фестиваль викингов, путь викингов, викинг, викинги, сула, ', '19-20 августа 2017 в живописном месте – Парке истории «Сула» прошел Второй международный фестиваль раннего средневековья «Путь Викингов». Фестиваль был посвящен эпохе легендарного торгового пути «из варяг в греки», который оказал огромное влияние на историю', 'Фестиваль "Путь викингов"', 1, 1),
(2, '12.07.2017', 'Фестиваль ретро авто «Ретро-Минск 2017»', 'retroavto21.jpg', '', '<p>Международный ежегодный фестиваль ретро автомобилей «Ретро-Минск 2017» прошел в Минске.</p>\r\n<p>Ford, Buick, Bmw, Rolls-Royce, Lincoln, Cadillac,Citroen, Mercedes и много чего еще!</p>\r\n<p>121 машина из 9-ти стран!</p>\r\n<p>Насладитесь удивительными фото в 360-градусов:</p>\r\n<style type="text/css">.kuula7lp4v { width: 100%; height: 640px; max-width: 100%; max-height: 100vw; }</style><iframe class="kuula7lp4v" frameborder="0" scrolling="no" allowfullscreen="true" src="https://kuula.co/share/7lp4V?thumbs=0&logo=0"></iframe>\r\n<style type="text/css">.kuula7lp4j { width: 100%; height: 640px; max-width: 100%; max-height: 100vw; }</style><iframe class="kuula7lp4j" frameborder="0" scrolling="no" allowfullscreen="true" src="https://kuula.co/share/7lp4j?thumbs=0&logo=0"></iframe>\r\n', 2, 'Международный фестиваль ретро автомобилей «Ретро-Минск 2017» прошел в Минске. 						Ford, Buick, Bmw, Rolls-Royce, Lincoln, Cadillac,Citroen, Mercedes и много чего еще!', 1, '<style type="text/css">.kuula7lp4g { width: 100%; height: 640px; max-width: 100%; max-height: 100vw; }</style><iframe class="kuula7lp4g" frameborder="0" scrolling="no" allowfullscreen="true" src="https://kuula.co/share/7lp4g?thumbs=0&logo=0"></iframe>', '2017-07-12 00:00:00', 'фестиваль, ретро автомобили, минск ретро, фестиваль в минске, автомобиль ретро минск', 'Международный фестиваль ретро автомобилей «Ретро-Минск 2017» прошел в Минске 12.07.2017.', 'Фестиваль ретро авто «Ретро-Минск 2017»', 1, 1),
(3, '12.06.2017', 'Дом Картин. Виртуальный тур', 'virtgallereya-740x370.jpg', '', '<p>Представляем вашему вниманию виртуальный тур по художественной галерее «Дом картин».</p>\r\n<p>Замечательная выставка «Запахи жизни-2», которая вдохновит и впечатлит каждого!</p>\r\n<p>Дом Картин — крупнейшая частная художественная галерея картин в Беларуси. Находится по адресу: г.Минск, проспект Победителей, 89/3.</p>\r\n<p>Для наилучшего просмотра включите расслабляющую музыку — наденьте очки виртуальной реальности(Google CardBoard) и запустите просмотр виртуального тура на своем мобильном телефоне нажав на иконку в панели управления</p>\r\n<p>Для того чтобы передвигаться по виртуальной галерее — просто наведите взгляд (центр) на значок <img width="15px" src="images/znachek.jpg" alt=""/>.</p>\r\n', 3, ' Виртуальный тур по художественной галерее «Дом картин». Замечательная выставка «Запахи жизни-2», которая вдохновит и впечатлит каждого!', 2, '<iframe src="http://domkartin.vrtip.top" width="800" height="450" frameborder="0" style="border:0" scrolling="no" allowfullscreen="true"></iframe>', '2017-06-12 00:00:00', '', '', 'Дом Картин. Виртуальный тур', 1, 1),
(4, '19.06.2017', '«Крылаты фэст» авиашоу под Минском', 'krylatyy_fest.png', '', '<p>17-18 июня под Минском прошел «Крылаты фэст» — захватывающее авиашоу с музыкальной программой, фуд-кортами, семейными развлечениями, обзорными авиапрогулками.</p>\r\n<p>На огромной территории музея народной архитектуры и быта развернулись многочисленные фудкорты, где продавали холодный квас шашлыки и даже можно было полакомиться шаурмой.</p>\r\n<style type="text/css">.kuula7lglv { width: 100%; height: 640px; max-width: 100%; max-height: 100vw; }</style><iframe class="kuula7lglv" frameborder="0" scrolling="no" allowfullscreen="true" src="https://kuula.co/share/7lGlV?thumbs=0&logo=0"></iframe>\r\n<p>Специально для детей была организована игровая зона (вход на авиашоу был бесплатным детям до 10 лет).\r\n</p>\r\n<p>На сцене в первый день выступали группы «Neuro Dubel» и «Без билета». На второй день 18 мая зрителей порадовали выступления групп «ŠUMA» и «J:MOPC».</p>\r\n<p>Место проведения авиафестиваля — тоже не случайно. Большая экспозиция представленная\r\nисторическими строениями — где можно погулять, посмотреть и даже зайти внутрь.</p>\r\n<p>«Крылаты фэст» — фестиваль гражданской авиации.</p>\r\n<p>Самолеты, вертолеты, аэростаты, автожиры, аэрошюты, мотопарапланы и дельтапланы — не только могли увидеть посетители авиашоу, но еще и сами совершить впечатляющий полет по доступным ценам.</p>\r\n', 3, '17-18 июня под Минском прошел «Крылаты фэст» — захватывающее авиашоу с музыкальной программой, фуд-кортами, семейными развлечениями, обзорными авиапрогулками. На огромной территории музея народной архитектуры и быта развернулись многочисленные фудкорты, г', 1, '<style type="text/css">.kuula7lglg { width: 100%; height: 640px; max-width: 100%; max-height: 100vw; }</style><iframe class="kuula7lglg" frameborder="0" scrolling="no" allowfullscreen="true" src="https://kuula.co/share/7lGlg?thumbs=0&logo=0"></iframe>', '2017-06-19 00:00:00', '', '', '«Крылаты фэст» авиашоу под Минском', 1, 1),
(5, '17.08.2017', 'Мемориал «Яма» на улице Мельникайте', 'sushe010a-1024x540.jpg', '', '<p>Мемориал «Яма» расположен на улице Мельникайте в Минске и посвящён жертвам Холокоста. Здесь 2 марта 1942 года нацистами было расстреляно около 5 000 узников минского гетто.</p>\r\n<p>2 марта 1942года большую группу евреев под конвоем погнали в сторону Дзержинска, и тех, кто не умер и не замерз насмерть по дороге, расстреляли, вероятнее всего, на территории Путчинского сельсовета. В этот же день из Минского гетто в железнодорожных вагонах были вывезены на запад и расстреляны ещё 3 412 евреев.</p>\r\n<p>Прямо в гетто после ухода трудоспособных узников заехали грузовые автомобили с немцами и полицаями, которые устроили очередное массовое убийство на всей территории гетто. Тела убитых — примерно 5 000 человек — сбрасывали в бывший карьер, на месте которого сейчас находится мемориал «Яма».\r\nВ этот же день, 2 марта, до 10 часов утра, не найдя достаточного количества людей для отправки на расстрел, немцы построили детей из детского дома и отвели на улицу Ратомскую, 35, где сбросили в яму. В это время к яме приехал Генеральный комиссар Белоруссии Вильгельм Кубе и бросал детям, которых живыми засыпали землей, конфеты. В этот день немцы убили от 200 до 300 детей вместе с медперсоналом и воспитательницами.</p>\r\n<p>В тот же день каратели расстреляли колонну людей, которые возвращались с работы. В исторической литературе эти события вошли под названием «бойня 2 марта».</p>\r\n<p>Обелиск мемориала «Яма» установлен в 1947 году. Текст надписи на идише написан поэтом Хаимом Мальтинским:\r\n« אַ ליכטיקער אָנדענק אפ אייביקע יאָרנ די פינפ טויזנט יידנ-קדוישימ וואָס זײַנענ דערמאָרדעט געוואָרנ דורכ די הענט פון די בלוטיקסטע סאָנימ פון דער מענשהײַט — די פאַשיסטיש־דײַטשישע מערדער־טאַליאָנימ‎‏‎ »</p>\r\n<p>Текст надписи на русском:\r\n«Светлая память на вечные времена пяти тысячам евреев, погибших от рук лютых врагов человечества — фашистско-немецких злодеев 2 марта 1942 года »\r\nЭто был первый памятник жертвам Холокоста в СССР, на котором было разрешено сделать надпись на идиш.</p>\r\n<p>В 2000 году установлена бронзовая скульптурная композиция «Последний путь», расположенная вдоль ступенек, ведущих к центру мемориала, и представляющая собой группу обречённых мучеников, спускающихся на дно ямы. Памятник создавался в течение 8 лет. Архитектор — Леонид Левин. Скульпторы — Александр Финский, Э. Полок.</p>\r\n<p>При выполнении работ по реконструкции мемориала не применялись машины и механизмы — все работы выполнялись вручную, в том числе и устройство фундамента скульптуры. Раскопки не проводились.</p>\r\n<h4>КАК ДОЕХАТЬ</h4>\r\n<p>Адрес: г. Минск, ул. Мельникайте, 11.<br/>\r\nМетро — станция «Фрунзенская».<br/>\r\nПроезд: Автобусы: 1, 163, 69, 73, 91.<br/>\r\nКоординаты: 53.909779, 27.542779</p>\r\n', 2, 'Мемориал «Яма» расположен на улице Мельникайте в Минске и посвящён жертвам Холокоста. Здесь 2 марта 1942 года нацистами было расстреляно около 5 000 узников минского гетто.', 2, '<iframe src="https://www.google.com/maps/embed?pb=!1m0!4v1508064019207!6m8!1m7!1sCAoSLEFGMVFpcE1XM21iaGJKQnZvQ0wzX3pFdXc1SWtFWVhCUkV6V3F3QXJpZ2NG!2m2!1d53.90988156681954!2d27.54298776215476!3f0.9449247732120902!4f-8.810390396084287!5f0.4000000000000002" width="100%" height="550" frameborder="0" style="border:0" allowfullscreen></iframe>', '2017-08-17 00:00:00', '', '', 'Мемориал «Яма» на улице Мельникайте', 1, 1),
(6, '25.05.2017', 'Универмаг «Беларусь»', 'univermagbel.jpg', '', '<p>«Универмаг Беларусь» — одно из самых крупных торговых предприятий Республики Беларусь.</p>\r\n<p>2 ноября 1970 г. была утвержден проект на строительство в Заводском районе города Минска универмага «Беларусь». 8 июня 1978 г. универмаг приветливо открыл свои двери для покупателей. В то время это был колоссальный проект.</p>\r\n<p>Во всей Беларуси не было аналогов среди торговых предприятий. Прекрасные залы, 43 секции, огромный ассортимент.\r\n</p>\r\n<iframe src="https://www.google.com/maps/embed?pb=!1m0!4v1508065470173!6m8!1m7!1sF%3A-qSRtG7x1a28%2FWRmdVE9U--I%2FAAAAAAAAAYw%2F20tdB8D9f-oUBEUR6KbovED8siKwWkNpwCLIB!2m2!1d53.87537004441432!2d27.62903017819776!3f42.40451685564947!4f-14.493049646549963!5f0.40755396808584476" width="100%" height="500" frameborder="0" style="border:0" allowfullscreen></iframe>\r\n<p>Многое поменялось с начала работы универмага: четыре раза он менял свой внешний вид ; изменялась примыкающая территория; создавались дополнительные дорожки, подъезды и подходы к универмагу; увеличивались торговые площади за счет складских и подсобных помещений; без остановки торгового процесса была проведена поэтапная реконструкция торгового зала с установкой новейшего , современного оборудования, для удобства покупателей была сделана замена старых эскалаторов на новые, работающие без шума.</p>\r\n<p>Улучшились и социально-бытовые условия для работников: в универмаге есть медицинский кабинет, конференц-зал на 340 посадочных мест,комната отдыха, спортивный зал, парикмахерская, библиотека, уютное кафе на 160 посадочных мест, сезонное кафе и бар.</p>\r\n<p>Ориентироваться в универмаге не трудно. Он по-настоящему универсален: в центре торговой площади каждого этажа находится современный эскалатор, к которому обращены входы-выходы секций. На первом этаже работает бюро справок, в котором ответят на все интересующие вопросы. Постоянные сообщения радиоузла позволят не растеряться в новостях , рекламных акциях и скидках, получить информацию о товарах и дополнительных услугах.</p>\r\n<p>В здании универмага есть банкоматы, пункты обмена валют, платежные терминалы, аптечный киоск, магазины цветов, салоны сотовой связи и др. Такая развитая инфраструктура предприятия торговли значительно бережет время покупателей, освобождая его для более приятных дел.</p>\r\n<p>«Универмаг Беларусь» расположен рядом со станцией метро «Партизанская». Сюда удобно добираться как на общественном транспорте, так и на личном. К услугам автомобилистов – бесплатная автостоянка.</p>\r\n<p>Официальный сайт универмага «Беларусь»: univermagbelarus.by</p>\r\n<h4>КАК ДОБРАТЬСЯ:</h4>\r\n<p>Адрес: г. Минск, ул. Жилуновича, 4.<br/>\r\nМетро — станция «Партизанская».<br/>\r\nПроезд: Автобусы: 59, 79, 93, 198э.<br/>\r\nТроллейбусы: 3, 16,17, 34, 67.<br/>\r\nКоординаты: 53.876078, 27.630030</p>', 1, '«Универмаг Беларусь» — одно из самых крупных торговых предприятий Республики Беларусь.', 2, '<iframe src="https://www.google.com/maps/embed?pb=!1m0!4v1508065295801!6m8!1m7!1sF%3A-U0BeD0xHz-U%2FWRmgoG8Z0QI%2FAAAAAAAAAZI%2FtJj0nPxTFu4ekpjZI0ZtvQ5iv-WF00NhACLIB!2m2!1d53.87535103578179!2d27.62998007189427!3f336.88!4f5.459999999999994!5f0.4000000000000002" width="100%" height="500" frameborder="0" style="border:0" allowfullscreen></iframe>', '2017-05-25 00:00:00', '', '', 'Универмаг «Беларусь»', 1, 1);

