--
-- Структура таблицы `city`
--

CREATE TABLE IF NOT EXISTS `city` (
  `city_id` int(11) NOT NULL AUTO_INCREMENT,
  `region_id` int(11) NOT NULL,
  `city_name` char(60) NOT NULL,
  `city_link` char(255) NOT NULL,
  `city_text` text NOT NULL,
  `city_description` text NOT NULL,
  `city_img` char(255) NOT NULL,
  `city_coord_lat` float NOT NULL,
  `city_coord_lng` float NOT NULL,
  `city_coord_center_lat` float NOT NULL,
  `city_coord_center_lng` float NOT NULL,
  `zoom` int(11) NOT NULL,
  `panorama_name` varchar(500) NOT NULL,
  `panorama_link` varchar(800) NOT NULL,
  `panorama_coords` char(255) NOT NULL,
  `title` char(255) NOT NULL,
  `position` int(11) NOT NULL,
  `visible` int(11) NOT NULL,
  PRIMARY KEY (`city_id`),
  KEY `city_id` (`city_id`),
  KEY `region_id` (`region_id`),
  KEY `city_id_2` (`city_id`),
  KEY `city_id_3` (`city_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;

--
-- Дамп данных таблицы `city`
--

INSERT INTO `city` (`city_id`, `region_id`, `city_name`, `city_link`, `city_text`, `city_description`, `city_img`, `city_coord_lat`, `city_coord_lng`, `city_coord_center_lat`, `city_coord_center_lng`, `zoom`, `panorama_name`, `panorama_link`, `panorama_coords`, `title`, `position`, `visible`) VALUES
(1, 7, 'Минск', 'city.php?id=1', '			<p>Минск (Менск, Менеск) возникает в середине XI в. как пограничная крепость Полоцкого княжества в месте слияния рек Свислочь и Немига.  Впервые упоминается в летописях под 1067 г. («Повесть временных лет»).  Весной этого года состоялась битва между войсками полоцкого князя Всеслава Брячиславовича (Всеслав Чародей) и тремя сынами киевского князя Ярослава Мудрого. Сражение, по мнению исследователей, произошло рядом с местом, где теперь стоит здание Белорусского государственного академического музыкального театра (ул. Мясникова, 44).</p>\r\n\r\n			<p>В начале XII в., при сыне Всеслава Брячиславовича Глебе Всеславовиче Минск становится центром удельного княжества. На протяжении второй половины XIII в. из летописей пропадают и город и княжество. Следующее упоминание датируется 1324 г. уже в составе Великого княжества Литовского.</p>\r\n\r\n			<p>Удачное расположение на пересечении главных дорог способствовало развитию Минска как крупного  торгового центра. Существовали рынки как постоянные, так и сезонные. В 1499 г. город получил Магдебургское право, т.е. право на самоуправление.</p>\r\n\r\n			<p>В 1569 г. Великое Княжество Литовское и Королевство Польское объединяются в Речь Посполитую. Минск – центр воеводства. С середины XVI в. минский городской вал постепенно теряет военное значение. В первой половине XIX в. последние городские укрепления были срыты.</p>\r\n\r\n			<p>С 1793 г. Минск в составе Российской империи. </p>\r\n\r\n			<p>В 1871 г. через город пролегла железная дорога Москва-Варшава, а двумя годами позднее еще одна,  связывающая порты Балтийского и Черного морей (Либаво-Роменская). Эти события сыграли важнейшую роль в дальнейшем развитии города. Сильный стимул получило развитие промышленности и строительства, появился водопровод. В конце XIX в. была построена электростанция, проведен телефон и запущено несколько веток конного трамвая. В 1898 г. в городе состоялся I съезд РСДРП.</p>\r\n\r\n			<p>Во время Первой Мировой войны Минск становится прифронтовым городом,  в нем размещаются армейские штабы, склады и госпитали. Уже после Октябрьской революции, 31 января 1919 г. Минск получил статус столицы.</p>\r\n\r\n			<p>Бурный рост экономики СССР в 30-е гг. XX в. и Минска в частности был прерван Великой Отечественной войной, во время, которой город был сильно разрушен. После войны развитие Минска продолжилось, построено большое количество предприятий легкой и тяжелой промышленности, учебных заведений, количественно и качественно вырос жилой фонд.</p>\r\n\r\n			<p>В 1974 г. присвоено почетное звание «Город-герой».</p>\r\n\r\n			<p>В 1984 г. запущена первая линия метро.</p>\r\n\r\n			<p>C 1991 года Минск является столицей государства — Республика Беларусь.</p>\r\n\r\n			<p>Сейчас Минск – самый большой как по площади: 348,84 км², так и по населению: 1 942 тыс. человек (на 1 января 2015 года) город Беларуси. Он является промышленным, экономическим, политическим, культурным и научным центром, а также крупнейшим транспортным узлом.</p>\r\n\r\n			<p>За свою почти тысячелетнюю официальную историю Минск пережил много маленьких и больших трагедий,    18 раз сгорал до тла, но как птица-феникс всегда возрождался из пепла, чтобы стать еще больше, еще лучше, еще красивее.</p>', '			<img src="images/cities/minsk/gerb_minsk.png" alt=""/>\n			<p class="bold">Столица Республики Беларусь</p>\n			<p>Дата основания: 1067г.</p>', 'cities/minsk/minskBiblioteka.jpg', 53.9045, 27.5615, 53.7976, 27.9089, 5, 'Панорама. Железнодорожный вокзал. Привокзальная площадь.', '<style type="text/css">.kuula7lkjm { width: 100%; height: 640px; max-width: 100%; max-height: 100vw; }</style><iframe class="kuula7lkjm" frameborder="0" scrolling="no" allowfullscreen="true" src="https://kuula.co/share/7lkjm?zoom=1&thumbs=0&logo=0"></iframe>', '53.891691, 27.551301', 'Минск', 1, 1);

