--
-- Дамп данных таблицы `helpful_info`
--

INSERT INTO `helpful_info` (`helpful_info_id`, `helpful_info_name`, `helpful_info_text`, `helpful_info_link`, `position`, `visible`, `title`, `keywords`, `description`) VALUES
(1, 'Виза в Беларусь', '<p>Большинству иностранных гостей для въезда в Беларусь необходимо получить один из видов виз (транзитная, краткосрочная/долгосрочная, однократная/многократная)</p>\r\n<p>В Беларуси начал действовать пятидневный безвизовый режим для граждан 80 стран.</p>\r\n<p>Отсчет пяти дней пребывания без визы в Беларуси идет с даты проставления штампа в паспорте</p>\r\n	\r\n	<h4>Кому необходима белорусская виза</h4>\r\n<p>Большинству иностранцев для посещения Беларуси (если срок пребывания превышает 5 дней) необходимо получить визу.</p>\r\n	<p class="underline">Виза не нужна гражданам следующих государств:</p>\r\n	<li>Азербайджан</li>\r\n	<li>Армения</li>\r\n	<li>Бразилия(для пребывания до двух раз в год длительностью до 90 дней)</li>\r\n	<li>Венесуэла(для пребывания длительностью до 90 дней)</li>\r\n	<li>Израиль(для пребывания длительностью до 90 дней в течение 6 месяцев с даты первого въезда)</li>\r\n	<li>Казахстан</li>\r\n	<li>Катар(для пребывания длительностью до 30 суток со дня въезда)</li>\r\n	<li>Куба (для пребывания длительностью до 30 дней)</li>\r\n	<li>Кыргызстан</li>\r\n	<li>Макао - специального административного района КНР (для пребывания до 30 дней)</li>\r\n	<li>Македония (при наличии приглашения или туристического ваучера)</li>\r\n	<li>Молдова</li>\r\n	<li>Монголия (для пребывания длительностью до 90 дней)</li>\r\n	<li>Объединенные Арабские Эмираты (для пребывания длительностью до 30 дней)</li>\r\n	<li>Россия</li>\r\n	<li>Сербия(для пребывания длительностью до 30 дней)</li>\r\n	<li>Таджикистан</li>\r\n	<li>Турция(для пребывания длительностью до 30 дней, общая продолжительность – до 90 дней в течение года от даты первого въезда)</li>\r\n	<li>Узбекистан</li>\r\n	<li>Украина</li>\r\n	<li>Черногория(для пребывания длительностью до 30 дней при наличии приглашения или туристического ваучера)</li>\r\n	<li>Эквадор(для пребывания до 30 дней с даты въезда, общая продолжительность – 90 дней в течение одного календарного года)</li>\r\n		\r\n	<p class="underline">Гражданам Китая безвизовый порядок установлен:</p>\r\n	<li>для въезда и выезда, транзитного пересечения и временного пребывания в Беларуси на срок до трех суток – в случае прибытия прямыми авиарейсами из аэропортов КНР, при наличии действительной визы государств – членов Европейского союза или государств – участников Шенгенской зоны;</li>\r\n	<li>для групповых туристических поездок (c 2016 г.).</li>', 'helpful_info.php', 1, 1, 'Виза в Беларусь', '', ''),
(2, 'Как приехать', '<p>Добраться в Беларусь воздушным, железнодорожным и автомобильным транспортом легко – существуют прямые маршруты из большинства европейских стран и стран СНГ.</p>\r\n<h4>Полет в Беларусь</h4>\r\n<p>Большинство путешественников прилетают в Беларусь. Национальный аэропорт Минск – крупнейший в стране. Это современный аэропорт с развитой инфраструктурой (банки, рестораны, магазины и др.).</p>\r\n<p>Национальная авиакомпания Беларуси "Белавиа" предлагает регулярные рейсы в крупнейшие города Европы и мира, в т.ч.:</p>\r\n<li>Абу-Даби</li>\r\n<li>Амстердам</li>\r\n<li>Астану</li>\r\n<li>Барселону</li>\r\n<li>Берлин</li>\r\n<li>Варшаву</li>\r\n<li>Вену</li>\r\n<li>Киев</li>\r\n<li>Лондон</li>\r\n<li>Москву</li>\r\n<li>Ригу</li>\r\n<li>Рим</li>\r\n<li>Санкт-Петербург</li>\r\n<li>Тбилиси</li>\r\n<li>Франкфурт</li>\r\n<li>Тель-Авив</li>\r\n<p>Аэропорт расположен почти в 30 км от Минска. После прохождения паспортного контроля вы можете добраться до города на:</p>\r\n<li>автобусе (курсируют каждый час);</li>\r\n<li>поезде (от аэровокзального комплекса на специальном рейсовом автобусе можно доехать до железнодорожного остановочного пункта Национальный аэропорт Минск, откуда курсируют поезда до Минска и станции Смолевичи); </li>\r\n<li>такси (около 25 евро);</li>\r\n<li>автомобиле напрокат (машину можно взять в аренду в зале прибытий).</li>\r\n<h4>В Беларусь на автомобиле</h4>\r\n<p>Можно приехать в Беларусь на автомобиле – в стране обширная сеть автомобильных дорог. Пересекать границу вам придется через какой-либо из пропускных пограничных пунктов.</p>\r\n<p>Для вождения автомобиля в Беларуси вам понадобится международное водительское удостоверение. Движение правостороннее. Ограничение скорости движения по автомагистралям – 110 км/ч.</p>\r\n<p>Надписи на дорожных знаках сделаны на кириллице. На знаках у крупных транспортных развязок используется латинский алфавит.</p>\r\n<p>В Беларуси действительны лишь некоторые виды иностранных полисов страхования туристов. Страховку можно приобрести на границе на 15 дней. Стоимость ее – от 5 евро.</p>\r\n<p>С 1 августа 2013 года в Беларуси действует электронная система сбора платы за проезд по автодорогам – BelToll.</p>\r\n', 'helpful_info.php', 2, 1, 'Как приехать в Беларусь', '', ''),
(3, 'Вождение в Беларуси', '', '', 3, 1, 'Вождение в Беларуси', '', ''),
(4, 'Таможенные правила', '', '', 4, 1, 'Таможенные правила в Беларуси', '', ''),
(5, 'Гостиницы', '', '', 5, 1, 'Гостиницы в Беларуси', '', ''),
(6, 'Транспорт', '', '', 6, 1, 'Транспорт в Беларуси', '', ''),
(7, 'Вопросы безопасности', '', '', 7, 1, 'Вопросы безопасности', '', ''),
(8, 'Мед.помощь, здоровье', '', '', 8, 1, 'Мед.помощь, здоровье', '', ''),
(9, 'Лечение в Беларуси', '', '', 9, 1, 'Лечение в Беларуси', '', ''),
(10, 'Санатории Беларуси', '', '', 10, 1, 'Санатории Беларуси', '', ''),
(11, 'Страхование', '', '', 11, 1, 'Страхование', '', '');

