INSERT INTO `rout` (`rout_id`, `rout_name`, `rout_time`, `rout_time_id`, `rout_img`, `rout_img_small`, `rout_link`, `rout_text`, `rout_rating`, `transport_id`, `rout_length`, `rout_path`, `rout_start_id`, `rout_coord_center_lat`, `rout_coord_center_lng`, `zoom`, `rout_description`, `position`, `visible`) VALUES
(1, 'Полоцкие и Витебские земли', '1-2 дня', 1, 'polotsk.jpg', 'polotsk.jpg', '', '<h3>Мемориальный комплекс «Хатынь»</h3>\r\n<p>Экскурсия посвящена событиям Великой Отечественной войны, долгим годам оккупации и борьбы белорусского народа против захватчиков. В день весеннего равноденствия, 22 марта 1943 года, произошла трагедия, ставшая символом страданий гражданского населения в годы Великой Отечественной войны — была сожжена затерявшаяся в лесах деревня ХАТЫНЬ вместе со 149 жителями. На месте трагедии в 1968 году архитекторы Ю. Градов, В. Занкович, Л. Левин и скульптор С. Селиханов создали величественный мемориал, ныне знаменитый на весь мир. Он создан в память сотен белорусских деревень, уничтоженных нацистами в годы Великой Отечественной войны и огромного вклада белорусского народа, принесшего неисчислимые жертвы — каждый 3-й житель Беларуси погиб в годы Великой Отечественной войны.  Мемориальный архитектурно-скульптурный комплекс, занимающий площадь около 50 га, получил планировочную схему бывшей деревни Хатыни. Формируется он из отдельных фрагментов. В центре композиции высится шестиметровая бронзовая скульптура «Непокоренный человек» с убитым ребенком на руках. Рядом сомкнутые гранитные плиты, символизирующие крышу сарая, в котором были сожжены жители деревни. Бывшая улица деревни выложена серыми, под цвет пепла, плитами. В тех местах, где стояли дома, поставлено 26 обелисков в виде опаленных огнем печных труб. На них — бронзовые таблички с именами тех, кто здесь родился и жил. А сверху — печально звенящие колокола. Здесь же находится единственное в мире Кладбище деревень; в память о жертвах фашистского террора горит Вечный огонь.</p>\r\n<h3>Полоцк</h3>\r\n<h4>Спасо-Евфросиниевский женский монастырь</h4>\r\n<p>Единственный сохранившийся до наших дней монастырь XII века, основанный принявшей\r\nмонашество полоцкой княжной Евфросиньей. Здесь находятся святые нетленные мощи\r\nпреподобной Евфросинии, а также вериги — цепи, которые носила на себе матушка при жизни. В\r\nархитектурный комплекс монастыря входят Спасо-Преображенская церковь (1161), Свято-\r\nКрестовоздвиженческий собор (1897), Евфросиниевская трапезная церковь (1847), ворота-\r\nзвонница (1882) с жилым монастырским корпусом.</p>\r\n<h4>Софийский собор</h4>\r\n<p>Собор Софии Премудрости Божией – один из самых ранних православных храмов в Европе и\r\nпервый каменный в Беларуси – включен в предварительный Список всемирного наследия\r\nЮНЕСКО. Возведенный в 1044 – 1066 годах по распоряжению князя Всеслава Чародея, он\r\nолицетворял независимое и могущественное Полоцкое княжество. Собор появился после своих\r\n"старших сестер" в Киеве и Новгороде, построенных по образцу собора в Константинополе, и стал\r\nчетвертым в мире храмом Святой Софии. В середине XVIII века он был значительно перестроен в\r\nсамобытном стиле виленского барокко.</p>\r\n<h4>Свято-Богоявленский собор</h4>\r\n<p>Центральный собор Свято-Богоявленского мужского\r\nмонастыря, основанного в XVI веке. Построен на\r\nместе сгоревшего деревянного собора в 1761-1779 гг.\r\nВ 1836 году стены и внутренняя поверхность купола\r\nсобора были расписаны фресками, фрагменты\r\nкоторых сохранились до настоящего времени. В\r\nсоборе находился знаменитый список чудотворной\r\nИверской Иконы Божьей Матери, авторство которого\r\nприписывалось евангелисту Луке. Впоследствии\r\nсобор неоднократно перестраивался. Является\r\nпамятником архитектуры барокко.</p>\r\n<h4>Храм в честь Покрова Пресвятой Богородицы</h4>\r\n<p>Свято-Покровский храм был построен в 1781 году и\r\nпервоначально являлся кладбищенской церковью\r\nБогоявленского монастыря. В начале 30-х годов ХХ\r\nвека Свято-Покровская церковь была закрыта\r\nсоветской властью. Служители храма были\r\nподвергнуты репрессиям. Архитектурная\r\nкомпозиция церкви выполнена в стиле\r\nтрадиционного русского зодчества. Двухъярусная\r\nшатровая звонница и зал для молитв создают\r\nощущение простоты и величия.</p>', 3, 2, '670 км', 'Минск — Хатынь — Полоцк — Витебск — Орша — Смоляны — Минск', 1, 54.7491, 28.8844, 6, '<div class="short_desc"> 		<div class="trip_duration"> 		<img src="images/icon/clock.png" alt=""/><p class="short">Длительность маршрута: 1 день</p></div> 		<div class="trip_duration"><img src="images/icon/peshkom.png" alt=""/><p class="short">Способ передвижения: пешком либо общественным транспортом</p>	</div> 		<div class="trip_duration"><img src="images/icon/speedometer_green.png" alt=""/><p class="short">Общая протяженность маршрута: 670 км.</p> 		</div> 	</div>', 1, 1),
(2, 'Руины дворцов и Беловежская Пуща', '3 дня', 2, 'belovezhskaya_pushha.jpg', 'belovezhskaya_pushha.jpg', '', '', 3, 1, '1000 км', 'Минск — Коссово — Ружаны — Пружаны — Каменец — Каменюки — Брест — Минск', 1, 0, 0, 0, '', 1, 1),
(3, 'Кревский и Гольшанский замки', '1 день', 1, 'castles/golshanskiyZamok2.jpg', 'castles/golshanskiyZamok2.jpg', '', '', 1, 3, '400 км', 'Минск — Крево — Боруны — Гольшаны — Минск', 1, 0, 0, 0, '', 1, 1),
(4, 'Храм-крепость и Жировичи', '3 дня', 2, 'Zhirovichi.jpg', 'Zhirovichi.jpg', '', '', 1, 2, '510 км', 'Минск — Слоним — Сынковичи — Жировичи — Минск', 1, 0, 0, 0, '', 1, 1),
(5, 'Мирский и Несвижский замки', '1 день', 1, 'belarus_42.jpg', 'belarus_42.jpg', '', '', 3, 2, '320км', 'Минск - Мир - Несвиж - Минск', 1, 0, 0, 0, '', 1, 1),
(6, 'Нарочь', '2 дня', 1, 'rout2.jpg', 'rout2.jpg', '', '', 1, 1, '410км', 'Минск - Нарочь - Минск', 1, 0, 0, 0, '', 1, 1),
(7, 'Знакомство с Минском', '1 день', 1, 'cities/minsk/minsk1.png', 'cities/minsk/minsk1.png', '', '', 2, 3, '35км', 'Минск', 1, 0, 0, 0, '', 0, 0),
(8, 'Курган Славы и Хатынь', '1 день', 1, 'hatun.JPG', 'hatun.JPG', '', '', 2, 2, '90км', 'Минск - Курган Славы - Хатынь - Минск', 1, 0, 0, 0, '', 0, 0);

